import react from "react";
import "./App.css";
import Navbar from "./components/Navbar/Navbar";
import Categories from "./components/categories/Categories";
import Store from "./components/store/Store";

class App extends react.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            Store: false,
            Gfilter: "",
            details: false,
        };
    }
    componentDidMount() {
        fetch("http://localhost:4000/db")
            .then((data) => data.json())
            .then((data) => {
                this.setState({ data: data });
                console.log(this.state.data);
            });
    }
    storeCallback = (data, filter = "") => {
        if (filter) {
            this.setState({
                Store: data,
                data: this.state.data,
                Gfilter: filter,
            });
        } else {
            this.setState({ Store: data, data: this.state.data, Gfilter: "" });
        }
        console.log(this.state);
    };
    render() {
        let componenet;
        if (this.state.data[0] && !this.state.Store) {
            componenet = (
                <Categories
                    className="Categories"
                    Storecallback={this.storeCallback}
                ></Categories>
            );
        } else if (this.state.Store) {
            componenet = (
                <Store
                    Data={this.state.data}
                    filter={this.state.Gfilter}
                ></Store>
            );
        } else {
            return <h1>loading</h1>;
        }

        return (
            <div className="App">
                <Navbar storeCallback={this.storeCallback}></Navbar>
                {componenet}
                <button
                    onClick={() => {
                        this.storeCallback(true);
                    }}
                >
                    Store
                </button>
            </div>
        );
    }
}

export default App;
