import react from "react";
import "./details.css";
function Details(Props) {
    function imageSlider(data) {
        document.querySelector(".bigImage").src;
    }
    return (
        <div className="detailContainer">
            <div className="detailCard">
                <div className="images">
                    <div className="littleImageswrapper">
                        {Props.item.images.map((item, index) => {
                            return (
                                <img
                                    src={item}
                                    onMouseEnter={() => {
                                        imageSlider(index);
                                    }}
                                    className="littleImages"
                                />
                            );
                        })}
                    </div>
                    <img className="bigImage" src={Props.item.images[0]} />
                </div>
                <div className="detailWrapper">
                    <div className="details detailItem">
                        <p className="name">
                            {Props.item.name} {num}
                        </p>
                        <p className="des">{Props.item.description}</p>
                        <p className="price mb0">{Props.item.price}Rs</p>
                    </div>
                    <div className="buy detailItem">
                        <button>Buy</button>
                        <button>Add to cart</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Details;
