import react from "react";
import "./details.css";
class Details extends react.Component {
    constructor(Props) {
        super(Props);
        this.state = {
            item: this.props.item,
        };
        this.imageSlider = this.imageSlider.bind(this);
    }
    imageSlider = (data) => {
        console.log(document.querySelector(".bigImage").src);
    };
    render() {
        console.log(this.state.item);

        return (
            <div className="detailContainer">
                <div className="detailCard">
                    <div className="images">
                        <div className="littleImageswrapper">
                            {this.Props.item.images.map((item, index) => {
                                return (
                                    <img
                                        src={item}
                                        onMouseEnter={() => {
                                            this.imageSlider(index);
                                        }}
                                        className="littleImages"
                                    />
                                );
                            })}
                        </div>
                        <img
                            className="bigImage"
                            // src={this.Props.item.images[0]}
                        />
                    </div>
                    <div className="detailWrapper">
                        <div className="details detailItem">
                            {/* <p className="name">{this.Props.item.name}s</p> */}
                            {/* <p className="des">{this.Props.item.description}</p> */}
                            {/* <p className="price mb0"> */}
                            {/* {this.Props.item.price}Rs */}
                            {/* </p> */}
                        </div>
                        <div className="buy detailItem">
                            <button>Buy</button>
                            <button>Add to cart</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Details;
